module.exports = function(that) {

    var re = /^\d+(?:(\.|,)\d{1,2})?$/;  //pgk Интересно, ако направя цифрите след запетаята незадължителни, в интернет валидаторите работи коректно, но тук не
    // var re = /^\d+(\.|,)\d{1,2}$/;

    //^\d+(\.\d{1,2})?$


    // var re = /^\d+\.?\d{1,2}$/;
    // var re = /^\d+((\.|,)?\d{1,2})?$/;
    // var re = /^(\d+(?:[.,]\d{1,2})?)$/;
    return re.test(that.value);
}
